작성중...vector&lt;int&gt; makeLPSTable(string pattern)
{ int patternSize = pattern.size(); vector&lt;int&gt; table(patternSize, 0); int left = 0; for (int right = 1; right &lt; patternSize; right++) { while (left &gt; 0 &amp;&amp; pattern[right] != pattern[left]) { left = table[left - 1]; } if (pattern[right] == pattern[left]) { table[right] = ++left; } } return table;
} void KMP(string target, string pattern)
{ vector&lt;int&gt; table = makeLPSTable(pattern); int targetLen = target.size(); int patternLen = pattern.size(); int j = 0; for (int i = 0; i &lt; targetLen; i++) { while (j &gt; 0 &amp;&amp; target[i] != pattern[j]) { j = table[j - 1]; } if (target[i] == pattern[j]) { if (j == patternLen - 1) { cout &lt;&lt; i - patternLen + 2 &lt;&lt; "번 위치에서 찾음"; j = table[j]; } else { j++; } } }
} int main()
{ string target = "ababacabacaabacaaba"; string pattern = "abacaaba"; KMP(target, pattern);
}

