{ if (cur_pos == r) { for (int i = 0; i &lt; R; ++i) cout &lt;&lt; pick[i] &lt;&lt; " "; cout &lt;&lt; "\n"; return; } for (int i = 0; i &lt; n; ++i) { if (!check[i]) { check[i] = true; pick[cur_pos] = nums[i]; Permutation(cur_pos + 1); check[i] = false; } }
} // 0번 자리부터
